import React from 'react';

function Header() {
  return (
    <header className="text-center my-8">
      <h1 className="text-4xl font-bold text-gray-800">Gemini Smart Scheduler </h1>
      <p className="text-gray-600">Let AI organize your day.</p>
    </header>
  );
}

export default Header;
